	/**
	 * @throws Exception
	 */
	@Test
	public void testZeroWatarmarkBits() throws Exception {
		int mask[] = {0xfffffe, 0xffffef, 0xfffeff, 0xffefff};
		int pixels[] = {0xffffff, 0xffffff, 0xffffff, 0xffffff};
		int zeroed[] = {0xfffffe, 0xffffef, 0xfffeff, 0xffefff};

		int result[] = Util.zeroWatarmarkBits(mask, pixels, (int) Math.sqrt(pixels.length), (int) Math.sqrt(pixels.length));

		assertEquals("Numbers of pixels shold be equal!", zeroed.length, result.length);
		for (int i = 0; i < zeroed.length && i < result.length; i++) {
			assertEquals("Pixels at index " + i + " are not equal!", zeroed[i], result[i]);
		}
	}

